#include "Zombie.hpp"

Zombie* newZombie(std::string name);
void	randomChump(std::string name);

int	main(void)
{
	std::string	cmd;
	std::string	name;

	std::cout << CLEAR << std::endl;
	std::cout << RED << "   ____                   _   _   _   _   _   _   _                                             ______" << std::endl;
	std::cout << "  |  _ \\                 (_) (_) (_) (_) (_) (_) (_)                                           | ___ /" << std::endl;
	std::cout << "  | |_) |  _ __    __ _   _   _   _   _   _   _   _   _ __    _ __    _ __    ____  ____  ____    / /" << std::endl;
	std::cout << "  |  _ <  | '__|  / _` | | | | | | | | | | | | | | | | '_ \\  | '_ \\  | '_ \\  |_  / |_  / |_  /   / /" << std::endl;
	std::cout << "  | |_) | | |    | (_| | | | | | | | | | | | | | | | | | | | | | | | | | | |  / /   / /   / /   / /__   _  _  _" << std::endl;
	std::cout << "  |____/  |_|     \\__,_| |_| |_| |_| |_| |_| |_| |_| |_| |_| |_| |_| |_| |_| /___| /___| /___| /_____| (_)(_)(_)" << std::endl;
	std::cout << std::endl;
	std::cout << std::endl;

	std::cout << WHITE << "\nName of the new Zombie : " << RED;
	std::getline(std::cin, name);
	std::cout << WHITE << "\n\nAllocate the zombies on the stack or heap ?\n" << std::endl;
	std::cout << GREEN << " [1] - Stack" << std::endl;
	std::cout << " [2] - Heap" << std::endl;
	std::cout << WHITE << "\nYour Choice : ";
	std::getline(std::cin, cmd);
	std::cout << std::endl;

	if (cmd == "1")
	{
		randomChump(name);
		std::cout << "*** return in the main **** \n" << std::endl;
	}
	else if (cmd == "2")
	{
		Zombie* zombiep = newZombie(name);
		zombiep->announce();
		delete zombiep;
		std::cout << CYANE << "*** Delete performed before this display **** \n" << std::endl;
	}
	else
		std::cout << RED << "\n" << cmd << RED << " : Choice not available  Bye :)" \
			<< std::endl << std::endl;

	std::cout << NONE;

	return (0);
}


